import pandas as pddf_questionnaires = pd.read_csv("../temp_data/q_office_df.csv")df_measurements = pd.read_csv("../temp_data/df_alz_means.csv")df_checklists = pd.read_csv("../temp_data/df_check.csv")#reindexdf_checklists = df_checklists.set_index('officeID',drop=False)df_measurements = df_measurements.set_index('officeID',drop=False)df_predictors = df_measurements.combine_first(df_checklists)df_predictors.reset_index(drop = True, inplace = True)# df_all = pd.merge(df_predictors,df_questionnaires, how='right', on="officeID")df_all = df_all.set_index('officeID',drop=False)# %%df_all.to_csv("../temp_data/df_all.csv", index=False)